// src/App.jsx

import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battleStarted, setBattleStarted] = useState(false);
  const [playerPokemon, setPlayerPokemon] = useState(null);
  const [opponentPokemon, setOpponentPokemon] = useState(null);
  const [battleResult, setBattleResult] = useState(null);

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  const handleStartBattle = () => {
    if (team.length < 2) {
      alert("You need at least 2 Pokémon on your team to start a battle.");
      return;
    }
    // Randomly select the two Pokémon for battle
    const player = team[0]; // Select the first Pokémon in the team
    const opponent = team[1]; // Select the second Pokémon in the team
    setPlayerPokemon(player);
    setOpponentPokemon(opponent);
    setBattleStarted(true);
  };

  const handleBattleEnd = (winner) => {
    setBattleResult(winner);
    setBattleStarted(false);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      <Pokedex onAddToTeam={addToTeam} />
      
      {/* Render Start Battle button only if we have at least two Pokémon */}
      {team.length >= 2 && !battleStarted && (
        <button onClick={handleStartBattle} style={{ marginTop: '1rem' }}>
          Start Battle
        </button>
      )}

      {/* Render the Battle component if battle has started */}
      {battleStarted && playerPokemon && opponentPokemon && (
        <Battle 
          playerPokemon={playerPokemon} 
          opponentPokemon={opponentPokemon} 
          onBattleEnd={handleBattleEnd} 
        />
      )}

      {/* Render the result of the battle */}
      {battleResult && (
        <div>
          <h3>Battle Over!</h3>
          <h4>Winner: {battleResult.name}</h4>
          <button onClick={() => setBattleStarted(false)}>Try Again</button>
        </div>
      )}
    </div>
  );
};

export default App;
