// src/components/Pokedex.jsx
import React, { useEffect, useState } from 'react';
import PokemonList from './PokemonList';

const Pokedex = ({ onAddToTeam }) => {
  const [pokemonList, setPokemonList] = useState([]);
  const selectedPokemonIds = [
    1, 4, 7, 10, 12, 16, 19, 25, 27, 35, 37, 39,
    41, 43, 52, 54, 58, 60, 63, 66, 69, 74, 77, 79
  ]; // Includes Pikachu (ID: 25)

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        const promises = selectedPokemonIds.map(id =>
          fetch(`https://pokeapi.co/api/v2/pokemon/${id}`).then(res => res.json())
        );
        const results = await Promise.all(promises);

        const formatted = results.map(p => ({
          id: p.id,
          name: p.name,
          image: p.sprites.front_default,
          stats: {
            hp: p.stats.find(s => s.stat.name === 'hp').base_stat,
            attack: p.stats.find(s => s.stat.name === 'attack').base_stat,
            defense: p.stats.find(s => s.stat.name === 'defense').base_stat,
            speed: p.stats.find(s => s.stat.name === 'speed').base_stat
          }
        }));

        setPokemonList(formatted);
      } catch (err) {
        console.error('Error fetching Pokémon:', err);
      }
    };

    fetchPokemon();
  }, []);

  return (
    <div>
      <h2>Choose Your Pokémon</h2>
      <PokemonList pokemons={pokemonList} onAddToTeam={onAddToTeam} />
    </div>
  );
};

export default Pokedex;
