// src/App.jsx

import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [opponent, setOpponent] = useState(null);
  const [battleResult, setBattleResult] = useState(null);
  const [isBattleStarted, setIsBattleStarted] = useState(false);

  const normalizeStats = (statsArray) => {
    const stats = {};
    statsArray.forEach(stat => {
      stats[stat.stat.name.toLowerCase()] = stat.base_stat;
    });
    return stats;
  };

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }

    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }

    const formattedPokemon = {
      ...pokemon,
      stats: normalizeStats(pokemon.stats),
    };

    setTeam([...team, formattedPokemon]);
  };

  const startBattle = () => {
    if (team.length === 0) {
      alert("Please add at least 1 Pokémon to your team to start a battle!");
      return;
    }

    const playerPokemon = team[0]; // Use first on the team
    const allOpponents = team.filter(p => p.id !== playerPokemon.id);
    const fallbackOpponent = {
      name: "Venusaur",
      image: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/3.png",
      stats: { hp: 80, attack: 82, defense: 83, speed: 80 },
    };

    const opponentPokemon = allOpponents.length > 0
      ? allOpponents[Math.floor(Math.random() * allOpponents.length)]
      : fallbackOpponent;

    const result = compareStats(playerPokemon, opponentPokemon);
    setOpponent(opponentPokemon);
    setBattleResult(result);
    setIsBattleStarted(true);
  };

  const compareStats = (player, opponent) => {
    const result = {};
    const keys = ['hp', 'attack', 'defense', 'speed'];

    keys.forEach(stat => {
      if (player.stats[stat] > opponent.stats[stat]) result[stat] = player.name;
      else if (player.stats[stat] < opponent.stats[stat]) result[stat] = opponent.name;
      else result[stat] = 'Tie';
    });

    const playerWins = Object.values(result).filter(winner => winner === player.name).length;
    const opponentWins = Object.values(result).filter(winner => winner === opponent.name).length;

    result.winner = playerWins > opponentWins ? `${player.name} Wins!` :
                     playerWins < opponentWins ? `${opponent.name} Wins!` : 'It\'s a Tie!';
    return result;
  };

  const handleNewBattle = () => {
    setIsBattleStarted(false);
    setBattleResult(null);
    setOpponent(null);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      {!isBattleStarted && (
        <>
         <button className="start-battle-btn" onClick={startBattle}>Start Battle</button>

          <Pokedex onAddToTeam={addToTeam} />
        </>
      )}
      {isBattleStarted && (
        <Battle
          playerPokemon={team[0]}
          opponentPokemon={opponent}
          battleResult={battleResult}
          onStartNewBattle={handleNewBattle}
        />
      )}
    </div>
  );
};

export default App;
