import React, { useState } from 'react';
import './Battle.css';  // Import the CSS file

const Battle = ({ playerPokemon, opponentPokemon, battleResult, onStartNewBattle }) => {
  const [battleOutcome, setBattleOutcome] = useState(null);

  // Ensure player and opponent Pokemon are provided
  if (!playerPokemon || !opponentPokemon) {
    return <div>Loading...</div>;
  }

  // Battle logic: we can add a very simple way to calculate the winner for now
  const calculateBattle = () => {
    const playerHP = playerPokemon.stats.find(stat => stat.name === 'hp').value;
    const opponentHP = opponentPokemon.stats.find(stat => stat.name === 'hp').value;

    let result = {
      hp: playerHP > opponentHP ? playerPokemon.name : opponentPokemon.name,
      attack: playerPokemon.stats.find(stat => stat.name === 'attack').value > opponentPokemon.stats.find(stat => stat.name === 'attack').value ? playerPokemon.name : opponentPokemon.name,
      defense: playerPokemon.stats.find(stat => stat.name === 'defense').value > opponentPokemon.stats.find(stat => stat.name === 'defense').value ? playerPokemon.name : opponentPokemon.name,
      speed: playerPokemon.stats.find(stat => stat.name === 'speed').value > opponentPokemon.stats.find(stat => stat.name === 'speed').value ? playerPokemon.name : opponentPokemon.name,
      winner: playerHP > opponentHP ? playerPokemon.name : opponentPokemon.name
    };

    setBattleOutcome(result);
  };

  return (
    <div className="battle-container">
      <h2 className="battle-header">Battle</h2>

      <div className="battle-stats">
        <h3>Stats Comparison:</h3>
        <table>
          <thead>
            <tr>
              <th>Stat</th>
              <th>{playerPokemon.name}</th>
              <th>{opponentPokemon.name}</th>
              <th>Winner</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>HP</td>
              <td>{playerPokemon.stats.find(stat => stat.name === 'hp').value}</td>
              <td>{opponentPokemon.stats.find(stat => stat.name === 'hp').value}</td>
              <td>{battleOutcome ? battleOutcome.hp : '...'}</td>
            </tr>
            <tr>
              <td>Attack</td>
              <td>{playerPokemon.stats.find(stat => stat.name === 'attack').value}</td>
              <td>{opponentPokemon.stats.find(stat => stat.name === 'attack').value}</td>
              <td>{battleOutcome ? battleOutcome.attack : '...'}</td>
            </tr>
            <tr>
              <td>Defense</td>
              <td>{playerPokemon.stats.find(stat => stat.name === 'defense').value}</td>
              <td>{opponentPokemon.stats.find(stat => stat.name === 'defense').value}</td>
              <td>{battleOutcome ? battleOutcome.defense : '...'}</td>
            </tr>
            <tr>
              <td>Speed</td>
              <td>{playerPokemon.stats.find(stat => stat.name === 'speed').value}</td>
              <td>{opponentPokemon.stats.find(stat => stat.name === 'speed').value}</td>
              <td>{battleOutcome ? battleOutcome.speed : '...'}</td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="battle-result">
        <h3>{battleOutcome ? `Winner: ${battleOutcome.winner}` : 'The battle is ongoing...'}</h3>
      </div>

      {/* Start Battle button */}
      {!battleOutcome && (
        <button className="start-btn" onClick={calculateBattle}>Start Battle</button>
      )}

      {/* Start New Battle button */}
      {battleOutcome && (
        <button className="start-battle-btn" onClick={onStartNewBattle}>Start New Battle</button>
      )}
    </div>
  );
};

export default Battle;
