import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [isBattleActive, setIsBattleActive] = useState(false);
  const [playerPokemon, setPlayerPokemon] = useState(null);
  const [opponentPokemon, setOpponentPokemon] = useState(null);
  const [battleResult, setBattleResult] = useState(null);

  // Add Pokémon to the team
  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  // Start battle function
  const startBattle = () => {
    if (team.length < 2) {
      alert("You need at least two Pokémon in your team to battle!");
      return;
    }
    // Randomly select Pokémon from the team for the battle
    const randomPlayer = team[Math.floor(Math.random() * team.length)];
    const randomOpponent = team[Math.floor(Math.random() * team.length)];
    setPlayerPokemon(randomPlayer);
    setOpponentPokemon(randomOpponent);
    setIsBattleActive(true);
  };

  // Handle new battle
  const startNewBattle = () => {
    setBattleResult(null);
    setIsBattleActive(false);
    setPlayerPokemon(null);
    setOpponentPokemon(null);
  };

  return (
    <div className="app-container">
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      {isBattleActive ? (
        <Battle
          playerPokemon={playerPokemon}
          opponentPokemon={opponentPokemon}
          battleResult={battleResult}
          onStartNewBattle={startNewBattle}
        />
      ) : (
        <>
          <Team team={team} />
          <Pokedex onAddToTeam={addToTeam} />
          <button className="start-battle-btn" onClick={startBattle}>Start Battle</button>
        </>
      )}
    </div>
  );
};

export default App;
