import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battle, setBattle] = useState(false);
  const [playerPokemon, setPlayerPokemon] = useState(null);
  const [opponentPokemon, setOpponentPokemon] = useState(null);

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);  // Add the whole Pokemon object to the team
  };

  const startBattle = () => {
    if (team.length < 2) {
      alert("You need at least two Pokémon to start a battle!");
      return;
    }

    const player = team[0];  // First Pokémon in team
    const opponent = team[1]; // Second Pokémon in team

    setPlayerPokemon(player);
    setOpponentPokemon(opponent);
    setBattle(true);
  };

  const startNewBattle = () => {
    setBattle(false);
    setPlayerPokemon(null);
    setOpponentPokemon(null);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      {!battle && <Pokedex onAddToTeam={addToTeam} />}
      {battle && <Battle 
        playerPokemon={playerPokemon} 
        opponentPokemon={opponentPokemon} 
        battleResult={{}} 
        onStartNewBattle={startNewBattle} 
      />}
      {!battle && <button onClick={startBattle}>Start Battle</button>}
    </div>
  );
};

export default App;
