// src/components/Team.jsx
import React from 'react';
import './Team.css';

const Team = ({ team }) => {
  return (
    <div className="team-container">
      {team.map((pokemon) => {
        const getStat = (name) => {
          const stat = pokemon.stats.find(s => s.stat.name === name);
          return stat ? stat.base_stat : 'N/A';
        };

        return (
          <div className="team-card" key={pokemon.id}>
            <img src={pokemon.image} alt={pokemon.name} />
            <h3>{pokemon.name}</h3>
            <p><strong>HP:</strong> {getStat('hp')}</p>
            <p><strong>Attack:</strong> {getStat('attack')}</p>
            <p><strong>Defense:</strong> {getStat('defense')}</p>
            <p><strong>Speed:</strong> {getStat('speed')}</p>
          </div>
        );
      })}
    </div>
  );
};

export default Team;
