import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battleResult, setBattleResult] = useState(null);
  const [selectedPokemon, setSelectedPokemon] = useState([]);

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  const startBattle = () => {
    if (team.length < 2) {
      alert("You need at least 2 Pokémon to start a battle!");
      return;
    }

    const pokemon1 = team[0];
    const pokemon2 = team[1];

    // Simulate battle based on stats (you can expand this logic further)
    const result = compareStats(pokemon1, pokemon2);
    setBattleResult(result);
  };

  const compareStats = (pokemon1, pokemon2) => {
    const stats1 = pokemon1.stats;
    const stats2 = pokemon2.stats;

    const pokemon1Wins = [stats1[0].base_stat, stats1[1].base_stat, stats1[2].base_stat];
    const pokemon2Wins = [stats2[0].base_stat, stats2[1].base_stat, stats2[2].base_stat];

    let score1 = 0;
    let score2 = 0;

    for (let i = 0; i < 3; i++) {
      if (pokemon1Wins[i] > pokemon2Wins[i]) score1++;
      if (pokemon1Wins[i] < pokemon2Wins[i]) score2++;
    }

    if (score1 > score2) {
      return `${pokemon1.name} wins!`;
    } else if (score2 > score1) {
      return `${pokemon2.name} wins!`;
    } else {
      return "It's a draw!";
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      <Pokedex onAddToTeam={addToTeam} />
      
      {/* Battle Section */}
      <div>
        <button onClick={startBattle} style={{ marginTop: '2rem', padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}>
          Start Battle
        </button>
        
        {/* Show Battle Result */}
        {battleResult && (
          <div style={{ marginTop: '2rem' }}>
            <h3>{battleResult}</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
