// src/components/PokemonCard.jsx
import React from 'react';
import './PokemonCard.css';  // Assuming the CSS file exists for your card styles

const PokemonCard = ({ pokemon, onAddToTeam }) => {
  const { id, name, image, stats } = pokemon;

  return (
    <div className="pokemon-card">
      <img src={image} alt={name} />
      <h3>{name}</h3>
      <p><strong>HP:</strong> {stats.hp}</p>
      <p><strong>Attack:</strong> {stats.attack}</p>
      <p><strong>Defense:</strong> {stats.defense}</p>
      <p><strong>Speed:</strong> {stats.speed}</p>
      <button onClick={() => onAddToTeam(pokemon)}>
        Add to Team
      </button>
    </div>
  );
};

export default PokemonCard;
