import React from 'react';
import './Battle.css';

const Battle = ({ playerPokemon, opponentPokemon, battleResult, onStartNewBattle }) => {
  return (
    <div className="battle-container">
      <h2>Battle Result</h2>
      <div className="battle">
        <div className="battle-pokemon">
          <img src={playerPokemon.image} alt={playerPokemon.name} />
          <p>{playerPokemon.name}</p>
          <p>HP: {playerPokemon.stats.hp}</p>
        </div>
        <div className="battle-pokemon">
          <img src={opponentPokemon.image} alt={opponentPokemon.name} />
          <p>{opponentPokemon.name}</p>
          <p>HP: {opponentPokemon.stats.hp}</p>
        </div>
      </div>
      <div className="battle-result">
        <p>{battleResult.winner}</p>
      </div>
      <button onClick={onStartNewBattle}>Start New Battle</button>
    </div>
  );
};

export default Battle;
