// src/components/Pokedex.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PokemonCard from './PokemonCard';
import './Pokedex.css';  // Import the corresponding CSS for Pokedex

const Pokedex = ({ onAddToTeam }) => {
  const [pokemonList, setPokemonList] = useState([]);

  useEffect(() => {
    const getPokemons = async () => {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=20');
      const results = response.data.results;

      const detailed = await Promise.all(
        results.map(async (p) => {
          const res = await axios.get(p.url);
          const stats = res.data.stats.reduce((acc, stat) => {
            acc[stat.stat.name] = stat.base_stat;
            return acc;
          }, {});

          return {
            id: res.data.id,
            name: res.data.name,
            image: res.data.sprites.front_default,
            stats,  // Ensure stats are correctly passed to each Pokémon object
            types: res.data.types.map(t => t.type.name),
          };
        })
      );

      setPokemonList(detailed);
    };

    getPokemons();
  }, []);

  return (
    <div className="pokedex-container">
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))', gap: '1rem' }}>
        {pokemonList.map(pokemon => (
          <PokemonCard key={pokemon.id} pokemon={pokemon} onAddToTeam={onAddToTeam} />
        ))}
      </div>
    </div>
  );
};

export default Pokedex;
