import React from 'react';
import './PokemonCard.css'; // Import the CSS file

const PokemonCard = ({ pokemon, onAddToTeam }) => {
  return (
    <div className="pokemon-card">
      <img src={pokemon.image} alt={pokemon.name} width={100} />
      <h3>{pokemon.name}</h3>
      
      {/* Display Pok√©mon stats */}
      <p><strong>HP:</strong> {pokemon.stats.hp}</p>
      <p><strong>Attack:</strong> {pokemon.stats.attack}</p>
      <p><strong>Defense:</strong> {pokemon.stats.defense}</p>
      <p><strong>Speed:</strong> {pokemon.stats.speed}</p>
      
      <button onClick={() => onAddToTeam(pokemon)} style={{ marginTop: '1rem' }}>
        Add to Team
      </button>
    </div>
  );
};

export default PokemonCard;
