import React, { useState } from 'react';
import axios from 'axios';

const AddToTeam = ({ pokemon }) => {
  const [message, setMessage] = useState('');

  const handleAddToTeam = () => {
    axios.post('http://localhost:5000/team', pokemon)
      .then(response => {
        setMessage(`${pokemon.name} added to your team!`);
      })
      .catch(err => {
        setMessage('Error adding Pokémon to team');
      });
  };

  return (
    <div>
      <button onClick={handleAddToTeam}>Add {pokemon.name} to Team</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddToTeam;
