import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';
import './index.css';

const App = () => {
  const [team, setTeam] = useState([]);  // Track user's team of Pokémon
  const [opponent, setOpponent] = useState(null);  // Track the opponent Pokémon
  const [battleResult, setBattleResult] = useState(null);  // Track the battle result
  const [battleStarted, setBattleStarted] = useState(false);  // Track if battle has started

  // Add a Pokémon to the team
  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  // Start a battle by selecting a random Pokémon from the team and a random opponent
  const startBattle = () => {
    if (team.length === 0) {
      alert("Add at least 1 Pokémon to your team before starting a battle!");
      return;
    }

    // Select a random Pokémon from the team to battle
    const playerPokemon = team[Math.floor(Math.random() * team.length)];

    // Select a random opponent from the first 151 Pokémon
    const randomIndex = Math.floor(Math.random() * 151) + 1;
    fetch(`https://pokeapi.co/api/v2/pokemon/${randomIndex}`)
      .then(res => res.json())
      .then(data => {
        const opponentPokemon = {
          name: data.name,
          image: data.sprites.front_default,
          stats: {
            hp: data.stats.find(s => s.stat.name === 'hp').base_stat,
            attack: data.stats.find(s => s.stat.name === 'attack').base_stat,
            defense: data.stats.find(s => s.stat.name === 'defense').base_stat,
            speed: data.stats.find(s => s.stat.name === 'speed').base_stat,
          }
        };

        setOpponent(opponentPokemon);
        setBattleResult(compareStats(playerPokemon, opponentPokemon));  // Compare stats
        setBattleStarted(true);  // Battle has started
      });
  };

  // Compare player and opponent stats to determine the winner
  const compareStats = (player, opponent) => {
    const result = {};
    let playerWins = 0;
    let opponentWins = 0;

    // Compare each stat
    ['hp', 'attack', 'defense', 'speed'].forEach(stat => {
      if (player.stats[stat] > opponent.stats[stat]) {
        result[stat] = player.name;
        playerWins++;
      } else {
        result[stat] = opponent.name;
        opponentWins++;
      }
    });

    result.winner = playerWins > opponentWins ? `${player.name} Wins!` : `${opponent.name} Wins!`;
    return result;
  };

  // Reset battle state when starting a new battle
  const handleStartNewBattle = () => {
    setOpponent(null);
    setBattleResult(null);
    setBattleStarted(false);  // Reset battle state
  };

  return (
    <div className="app-container">
      <Header />

      {/* Start Battle Button */}
      {!battleStarted && (
        <button className="start-battle-button" onClick={startBattle}>
          Start Battle
        </button>
      )}

      {/* Show Team */}
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />

      {/* Show Battle if started */}
      {battleStarted && opponent && battleResult && (
        <Battle
          playerPokemon={team[Math.floor(Math.random() * team.length)]}
          opponentPokemon={opponent}
          battleResult={battleResult}
          onStartNewBattle={handleStartNewBattle}
        />
      )}

      {/* Pokedex */}
      <Pokedex onAddToTeam={addToTeam} />
    </div>
  );
};

export default App;
