import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battlePokemons, setBattlePokemons] = useState([null, null]);  // [pokemon1, pokemon2]

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  // Function to select Pokémon for the battle
  const handleSelectForBattle = (pokemon, slot) => {
    const updatedBattlePokemons = [...battlePokemons];
    updatedBattlePokemons[slot] = pokemon;  // Update the selected Pokémon for battle
    setBattlePokemons(updatedBattlePokemons);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />

      {/* Choose two Pokémon for battle */}
      <h3>Select Pokémon for Battle</h3>
      <div style={{ display: 'flex', gap: '1rem' }}>
        <div>
          <h4>Pokémon 1</h4>
          {battlePokemons[0] ? (
            <div>
              <img src={battlePokemons[0].image} alt={battlePokemons[0].name} width={50} />
              <div>{battlePokemons[0].name}</div>
            </div>
          ) : (
            <p>Choose Pokémon for Battle</p>
          )}
          <select onChange={(e) => handleSelectForBattle(team[e.target.selectedIndex], 0)}>
            <option>Select a Pokémon</option>
            {team.map((pokemon, index) => (
              <option key={pokemon.id} value={index}>{pokemon.name}</option>
            ))}
          </select>
        </div>

        <div>
          <h4>Pokémon 2</h4>
          {battlePokemons[1] ? (
            <div>
              <img src={battlePokemons[1].image} alt={battlePokemons[1].name} width={50} />
              <div>{battlePokemons[1].name}</div>
            </div>
          ) : (
            <p>Choose Pokémon for Battle</p>
          )}
          <select onChange={(e) => handleSelectForBattle(team[e.target.selectedIndex], 1)}>
            <option>Select a Pokémon</option>
            {team.map((pokemon, index) => (
              <option key={pokemon.id} value={index}>{pokemon.name}</option>
            ))}
          </select>
        </div>
      </div>

      {/* If both Pokémon are selected, show the battle */}
      {battlePokemons[0] && battlePokemons[1] && (
        <Battle pokemon1={battlePokemons[0]} pokemon2={battlePokemons[1]} />
      )}

      <Pokedex onAddToTeam={addToTeam} />
    </div>
  );
};

export default App;
