import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battleResult, setBattleResult] = useState(null);

  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  const startBattle = () => {
    if (team.length < 2) {
      alert("You need at least 2 Pokémon to start a battle!");
      return;
    }

    const pokemon1 = team[0];
    const pokemon2 = team[1];

    // Compare stats
    const result = compareStats(pokemon1, pokemon2);
    setBattleResult(result);
  };

  const compareStats = (pokemon1, pokemon2) => {
    const stats1 = pokemon1.stats;
    const stats2 = pokemon2.stats;

    // Collect the battle results for HP, Attack, and Speed
    const statComparison = [
      { name: 'HP', pokemon1Stat: stats1[0].base_stat, pokemon2Stat: stats2[0].base_stat },
      { name: 'Attack', pokemon1Stat: stats1[1].base_stat, pokemon2Stat: stats2[1].base_stat },
      { name: 'Speed', pokemon1Stat: stats1[5].base_stat, pokemon2Stat: stats2[5].base_stat },
    ];

    // Determine who wins each stat
    const comparisonResults = statComparison.map((stat) => {
      let winner = null;
      if (stat.pokemon1Stat > stat.pokemon2Stat) {
        winner = pokemon1.name;
      } else if (stat.pokemon1Stat < stat.pokemon2Stat) {
        winner = pokemon2.name;
      } else {
        winner = 'Draw';
      }

      return {
        statName: stat.name,
        pokemon1Stat: stat.pokemon1Stat,
        pokemon2Stat: stat.pokemon2Stat,
        winner: winner,
      };
    });

    // Calculate final winner
    const pokemon1Wins = comparisonResults.filter(result => result.winner === pokemon1.name).length;
    const pokemon2Wins = comparisonResults.filter(result => result.winner === pokemon2.name).length;

    const finalWinner = pokemon1Wins > pokemon2Wins ? pokemon1.name : (pokemon2Wins > pokemon1Wins ? pokemon2.name : 'Draw');

    return { comparisonResults, finalWinner };
  };

  const tryAgain = () => {
    setBattleResult(null);
    setTeam([]);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      <Pokedex onAddToTeam={addToTeam} />
      
      {/* Battle Section */}
      <div>
        <button onClick={startBattle} style={{ marginTop: '2rem', padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}>
          Start Battle
        </button>
        
        {/* Show Battle Result */}
        {battleResult && (
          <div style={{ marginTop: '2rem' }}>
            <h3>Battle Results</h3>
            <table border="1" style={{ width: '100%', marginTop: '1rem', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th>Stat</th>
                  <th>{team[0]?.name}</th>
                  <th>{team[1]?.name}</th>
                  <th>Winner</th>
                </tr>
              </thead>
              <tbody>
                {battleResult.comparisonResults.map((result, index) => (
                  <tr key={index}>
                    <td>{result.statName}</td>
                    <td>{result.pokemon1Stat}</td>
                    <td>{result.pokemon2Stat}</td>
                    <td>{result.winner}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <h3 style={{ marginTop: '1rem' }}>Final Winner: {battleResult.finalWinner}</h3>
            <button onClick={tryAgain} style={{ marginTop: '1rem', padding: '10px 20px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '5px' }}>
              Try Again
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
