// src/components/PokemonCard.jsx
import React from 'react';

const PokemonCard = ({ pokemon, onAddToTeam }) => {
  return (
    <div style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '1rem', textAlign: 'center' }}>
      <img src={pokemon.image} alt={pokemon.name} width={100} />
      <h3>{pokemon.name}</h3>
      <p>HP: {pokemon.stats.hp}</p>
      <p>Attack: {pokemon.stats.attack}</p>
      <button onClick={() => onAddToTeam(pokemon)} style={{ marginTop: '1rem' }}>
        Add to Team
      </button>
    </div>
  );
};

export default PokemonCard;
