import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battlePokemons, setBattlePokemons] = useState([null, null]);  // [pokemon1, pokemon2]
  const [battleResult, setBattleResult] = useState(null);  // To store battle result
  const [battleStarted, setBattleStarted] = useState(false);  // To control battle start

  // Function to add Pokémon to the team
  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  // Function to select Pokémon for the battle
  const handleSelectForBattle = (pokemon, slot) => {
    const updatedBattlePokemons = [...battlePokemons];
    updatedBattlePokemons[slot] = pokemon;  // Update the selected Pokémon for battle
    setBattlePokemons(updatedBattlePokemons);
  };

  // Function to start the battle
  const startBattle = () => {
    if (battlePokemons[0] && battlePokemons[1]) {
      setBattleStarted(true);
      setBattleResult(null);  // Reset battle result before starting
    } else {
      alert("Please select two Pokémon for the battle!");
    }
  };

  // Function to reset battle
  const resetBattle = () => {
    setBattlePokemons([null, null]);
    setBattleStarted(false);
    setBattleResult(null);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />

      {/* Choose two Pokémon for battle */}
      <h3>Select Pokémon for Battle</h3>
      <div style={{ display: 'flex', gap: '1rem' }}>
        <div>
          <h4>Pokémon 1</h4>
          {battlePokemons[0] ? (
            <div>
              <img src={battlePokemons[0].image} alt={battlePokemons[0].name} width={50} />
              <div>{battlePokemons[0].name}</div>
            </div>
          ) : (
            <p>Choose Pokémon for Battle</p>
          )}
          <select onChange={(e) => handleSelectForBattle(team[e.target.selectedIndex], 0)}>
            <option>Select a Pokémon</option>
            {team.map((pokemon, index) => (
              <option key={pokemon.id} value={index}>{pokemon.name}</option>
            ))}
          </select>
        </div>

        <div>
          <h4>Pokémon 2</h4>
          {battlePokemons[1] ? (
            <div>
              <img src={battlePokemons[1].image} alt={battlePokemons[1].name} width={50} />
              <div>{battlePokemons[1].name}</div>
            </div>
          ) : (
            <p>Choose Pokémon for Battle</p>
          )}
          <select onChange={(e) => handleSelectForBattle(team[e.target.selectedIndex], 1)}>
            <option>Select a Pokémon</option>
            {team.map((pokemon, index) => (
              <option key={pokemon.id} value={index}>{pokemon.name}</option>
            ))}
          </select>
        </div>
      </div>

      {/* "Start Battle" button */}
      <button 
        onClick={startBattle} 
        style={{
          marginTop: '1rem',
          backgroundColor: '#4CAF50',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Start Battle
      </button>

      {/* Show Battle Result */}
      {battleStarted && battlePokemons[0] && battlePokemons[1] && !battleResult && (
        <Battle pokemon1={battlePokemons[0]} pokemon2={battlePokemons[1]} setBattleResult={setBattleResult} />
      )}

      {/* Show Battle Result and "Try Again" button */}
      {battleResult && (
        <div>
          <h2>Battle Result: {battleResult}</h2>
          <button 
            onClick={resetBattle} 
            style={{
              marginTop: '1rem',
              backgroundColor: '#FF6347',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            Try Again
          </button>
        </div>
      )}

      <Pokedex onAddToTeam={addToTeam} />
    </div>
  );
};

export default App;
