import React, { useState, useEffect } from 'react';

const Battle = ({ playerPokemon, opponentPokemon, onBattleEnd }) => {
  const [playerHp, setPlayerHp] = useState(playerPokemon.hp); // Use direct hp value
  const [opponentHp, setOpponentHp] = useState(opponentPokemon.hp); // Use direct hp value
  const [turnMessage, setTurnMessage] = useState('');
  const [round, setRound] = useState(1);
  const [battleFinished, setBattleFinished] = useState(false);

  // Damage calculation based on Attack vs Defense (simplified formula)
  const calculateDamage = (attacker, defender) => {
    const attack = attacker.attack;
    const defense = defender.defense;
    const randomFactor = Math.random() * 0.1 + 0.85;  // Random factor to vary damage output
    return Math.max(1, Math.floor((attack / defense) * randomFactor));
  };

  const startBattle = () => {
    let currentPlayerHp = playerHp;
    let currentOpponentHp = opponentHp;

    const playerSpeed = playerPokemon.speed;
    const opponentSpeed = opponentPokemon.speed;

    const playerFirst = playerSpeed >= opponentSpeed;  // Determine who goes first

    let currentRound = 1;
    const battleInterval = setInterval(() => {
      if (currentRound > 5) {  // Limit the battle to 5 rounds
        clearInterval(battleInterval);
        return;
      }

      // Display turn-based messages
      setTurnMessage(`Round ${currentRound}:`);

      let damageToOpponent = calculateDamage(playerPokemon, opponentPokemon);
      let damageToPlayer = calculateDamage(opponentPokemon, playerPokemon);

      if (playerFirst) {
        // Player attacks first
        currentOpponentHp -= damageToOpponent;
        if (currentOpponentHp <= 0) {
          currentOpponentHp = 0;
          clearInterval(battleInterval);
          onBattleEnd(playerPokemon);  // Player wins
          return;
        }

        // Opponent attacks after player
        currentPlayerHp -= damageToPlayer;
        if (currentPlayerHp <= 0) {
          currentPlayerHp = 0;
          clearInterval(battleInterval);
          onBattleEnd(opponentPokemon);  // Opponent wins
          return;
        }
      } else {
        // Opponent attacks first
        currentPlayerHp -= damageToPlayer;
        if (currentPlayerHp <= 0) {
          currentPlayerHp = 0;
          clearInterval(battleInterval);
          onBattleEnd(opponentPokemon);  // Opponent wins
          return;
        }

        // Player attacks after opponent
        currentOpponentHp -= damageToOpponent;
        if (currentOpponentHp <= 0) {
          currentOpponentHp = 0;
          clearInterval(battleInterval);
          onBattleEnd(playerPokemon);  // Player wins
          return;
        }
      }

      setPlayerHp(currentPlayerHp);
      setOpponentHp(currentOpponentHp);
      setRound(currentRound + 1);
      currentRound++;
    }, 1000);  // Simulate battle every second
  };

  // Start battle when the component mounts
  useEffect(() => {
    startBattle();
  }, []);

  // Get stat values safely
  const getStatValue = (pokemon, stat) => {
    const statObj = pokemon.stats.find(s => s.stat.name === stat); // Find the stat by name
    return statObj ? statObj.base_stat : 0;  // Return the stat value or 0 if not found
  };

  return (
    <div>
      <h2>Battle in Progress!</h2>
      <p>{turnMessage}</p>
      <div>
        <h3>Player's Pokémon: {playerPokemon.name} | HP: {playerHp}</h3>
        <h3>Opponent's Pokémon: {opponentPokemon.name} | HP: {opponentHp}</h3>
      </div>
      <div>
        <h4>Stats Comparison:</h4>
        <table>
          <thead>
            <tr>
              <th>Stat</th>
              <th>{playerPokemon.name}</th>
              <th>{opponentPokemon.name}</th>
              <th>Winner</th>
            </tr>
          </thead>
          <tbody>
            {['hp', 'attack', 'defense', 'speed'].map(stat => {
              const playerStatValue = getStatValue(playerPokemon, stat);
              const opponentStatValue = getStatValue(opponentPokemon, stat);
              const winner = playerStatValue > opponentStatValue ? playerPokemon.name : opponentPokemon.name;
              return (
                <tr key={stat}>
                  <td>{stat.charAt(0).toUpperCase() + stat.slice(1)}</td>
                  <td>{playerStatValue}</td>
                  <td>{opponentStatValue}</td>
                  <td>{winner}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Battle;
