import React from 'react';
import './Pokedex.css';

const Pokedex = ({ onAddToTeam }) => {
  const pokemonList = [
    { id: 1, name: 'Bulbasaur', stats: { hp: 45, attack: 49, defense: 49, speed: 45 } },
    { id: 2, name: 'Ivysaur', stats: { hp: 60, attack: 62, defense: 63, speed: 60 } },
    // Add other Pokémon data here...
  ];

  const handleAddToTeam = (pokemon) => {
    onAddToTeam(pokemon);  // This will add the Pokémon to the team
  };

  return (
    <div className="pokedex-container">
      {pokemonList.map(pokemon => (
        <div key={pokemon.id} className="pokemon-card">
          <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.id}.png`} alt={pokemon.name} />
          <h3>{pokemon.name}</h3>
          <p>HP: {pokemon.stats.hp}</p>
          <p>Attack: {pokemon.stats.attack}</p>
          <p>Defense: {pokemon.stats.defense}</p>
          <p>Speed: {pokemon.stats.speed}</p>
          <button onClick={() => handleAddToTeam(pokemon)}>Add to Team</button>
        </div>
      ))}
    </div>
  );
};

export default Pokedex;
