// src/components/PokemonCard.jsx

import React from 'react';

const PokemonCard = ({ pokemon, onAddToTeam }) => {
  return (
    <div style={{
      background: '#fff',
      borderRadius: '8px',
      padding: '1rem',
      textAlign: 'center',
      border: '1px solid #ccc',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    }}>
      {/* Pokémon Image */}
      <img src={pokemon.image} alt={pokemon.name} style={{ width: '100px', height: '100px' }} />
      
      {/* Pokémon Name */}
      <h3>{pokemon.name}</h3>

      {/* Pokémon Types */}
      <p>Types: {pokemon.types.join(', ')}</p>

      {/* Button to Add Pokémon to Team */}
      <button 
        onClick={() => onAddToTeam(pokemon)}
        style={{
          backgroundColor: '#4CAF50',
          color: 'white',
          padding: '8px 16px',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Add to Team
      </button>
    </div>
  );
};

export default PokemonCard;
