import React, { useState } from 'react';
import Header from './components/Header';
import Pokedex from './components/Pokedex';
import Team from './components/Team';
import Battle from './components/Battle';

const App = () => {
  const [team, setTeam] = useState([]);
  const [battleActive, setBattleActive] = useState(false);
  const [playerPokemon, setPlayerPokemon] = useState(null);
  const [opponentPokemon, setOpponentPokemon] = useState(null);
  const [battleResult, setBattleResult] = useState(null);

  // Add Pokemon to Team
  const addToTeam = (pokemon) => {
    if (team.length >= 6) {
      alert("You can only have 6 Pokémon on your team!");
      return;
    }
    if (team.find(p => p.id === pokemon.id)) {
      alert("This Pokémon is already on your team!");
      return;
    }
    setTeam([...team, pokemon]);
  };

  // Start a battle
  const startBattle = () => {
    if (team.length < 2) {
      alert("You need at least 2 Pokémon on your team to battle!");
      return;
    }

    const opponent = team[Math.floor(Math.random() * team.length)]; // Randomly select an opponent
    const player = team[0]; // Choose the first Pokémon as the player Pokémon

    setPlayerPokemon(player);
    setOpponentPokemon(opponent);
    setBattleActive(true);
    setBattleResult(null);  // Reset the battle result
  };

  // End battle and set result
  const endBattle = (winner) => {
    setBattleResult(winner);
    setBattleActive(false);
  };

  // Try again button callback
  const tryAgain = () => {
    setBattleResult(null);
    setBattleActive(false);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <Header />
      <h2>My Team ({team.length}/6)</h2>
      <Team team={team} />
      <Pokedex onAddToTeam={addToTeam} />
      
      {team.length >= 2 && !battleActive && (
        <button onClick={startBattle}>Start Battle</button>
      )}

      {battleActive && (
        <Battle
          playerPokemon={playerPokemon}
          opponentPokemon={opponentPokemon}
          onBattleEnd={endBattle}
        />
      )}

      {battleResult && (
        <div>
          <h3>{battleResult.name} Wins!</h3>
          <button onClick={tryAgain}>Try Again</button>
        </div>
      )}
    </div>
  );
};

export default App;
